!function(){F.context('staticController').run(function () {

	$.ajaxSetup({
		cache : false
	});

	//2014_10_11 zhangyijun02: 因增加广告轮播曝光日志逻辑，调整showtrack初始化方式
	require.async(['common:widget/ui/usertrack/usertrack.js', 'common:widget/ui/lazyload/lazyload.js', 'common:widget/ui/cookie/cookie.js', 'common:widget/ui/showtrack/showtrack.js', 'common:widget/ui/items_post/items_post.js'], function(usertrack, LazyLoad, Cookie, ShowTrack, itemsPost){
		// 初始化点击监听
		usertrack.init();

		new LazyLoad({
			lazy : $('img[data-original]'),
			expect: 250
		});

		itemsPost.init();

		handleBaiduid();

		function handleBaiduid(){
			var getBaiduidUrl = 'http://uid.nuomi.baidu.com/getbdid'; // 
			var retryCount = 1;
			getBaiduid();

			function getBaiduid(){

				$.ajax(getBaiduidUrl, {
					dataType : 'jsonp',
					success : function(json){
						if(json.errno == 0){
							var baiduid = json.data.baiduid;
							if(baiduid){
								Cookie.setRaw('BAIDUID', baiduid, 365);
							}
							// 不管有没有baiduid，都发送pv
							sendPv();
						}else{
							if(retryCount--){
								getBaiduid();
							}else{
								// 最后一次也获取失败
								sendPv();
							}
						}

					}
				});
			}


		}

		function sendPv(){
			usertrack.send({
				da_src : encodeURIComponent($.stringify({
					page: 'order_list' // 
				})),
				element_type : "pv"
			});
		}

		//2014_10_11 zhangyijun02: 因增加广告轮播曝光日志逻辑，调整ShowTrack初始化方式
		//曝光日志统计
		ShowTrack.startShowTrack();
	});

});}();</script><script type="text/javascript">(new Image()).src="http://static.tieba.baidu.com/tb/pms/img/st.gif?pid=242&v=2&fs=18672ae,96f3df1,b3f15f4,b372038,9631f41,7929373,e251416,7945b43,20b7306,937742a,a9585ed,1a4bec3,ae59b09,1dd3170,e1aa050,eeb19ae,3232dd9,c3028bf,bd2a35e,6da1d66&otherStr=&page=user/page/order_list.tpl&sid=1430237842&hash=cf31882670&fid=baidunuomi-pc";</script>
